import re
import functools


@functools.lru_cache(maxsize=None)
def expa(mol, it, mit):
	if mol == "e":
		print("found it at", it)
		exit()
	if it > mit:
		return
	for k in rules:
		for v in rules[k]:
			for x in re.finditer(k, mol):
				n = mol[0:x.start()] + v + mol[x.end():]
				expa(n, it+1, mit)

